import React, { useContext, useState } from "react";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import Sidebar from "../../img/sidebar.png";
import Ecommerce from "../../img/ecommerce.png";
import HOC from "../../img/hoc.png";
import MusicApp from "../../img/musicapp.png";
import video1 from "../../img/video1.mp4"; // Adjust paths to your actual video files
import video2 from "../../img/video2.mp4";
import video3 from "../../img/video3.mp4";
import video4 from "../../img/video4.mp4";
import { themeContext } from "../../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;
  const [currentVideo, setCurrentVideo] = useState(null);

  const openVideo = (videoUrl) => {
    setCurrentVideo(videoUrl);
  };

  return (
    <div className="portfolio" id="portfolio">
      {/* heading */}
      <span style={{ color: darkMode ? "white" : "" }}>Recent Projects</span>
      <span>Portfolio</span>

      {/* slider */}
      <Swiper
        spaceBetween={30}
        slidesPerView={3}
        grabCursor={true}
        className="portfolio-slider"
      >
        {/* Sidebar Image */}
        <SwiperSlide>
          <div className="portfolio-item">
            <img src={Sidebar} alt="" />
            <button
              className="portfolio-button"
              onClick={() => openVideo(video1)}
            >
              Play Video
            </button>
          </div>
        </SwiperSlide>

        {/* Ecommerce Image */}
        <SwiperSlide>
          <div className="portfolio-item">
            <img src={Ecommerce} alt="" />
            <button
              className="portfolio-button"
              onClick={() => openVideo(video2)}
            >
              Play Video
            </button>
          </div>
        </SwiperSlide>

        {/* MusicApp Image */}
        <SwiperSlide>
          <div className="portfolio-item">
            <img src={MusicApp} alt="" />
            <button
              className="portfolio-button"
              onClick={() => openVideo(video3)}
            >
              Play Video
            </button>
          </div>
        </SwiperSlide>

        {/* HOC Image */}
        <SwiperSlide>
          <div className="portfolio-item">
            <img src={HOC} alt="" />
            <button
              className="portfolio-button"
              onClick={() => openVideo(video4)}
            >
              Play Video
            </button>
          </div>
        </SwiperSlide>
      </Swiper>

      {/* Video Player */}
      {currentVideo && (
        <div className="video-player">
          <video width="600" controls autoPlay>
            <source src={currentVideo} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}
    </div>
  );
};

export default Portfolio;
