import React, { useContext } from "react";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import Video1 from "../../videos/video1.mp4";
import Video2 from "../../videos/video2.mp4";
import Video3 from "../../videos/video3.mp4";
import Video4 from "../../videos/video4.mp4";
import Video5 from "../../videos/video5.mp4";
import { themeContext } from "../../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  return (
    <div className="portfolio" id="portfolio">
      {/* heading */}
      <span style={{ color: darkMode ? 'white' : '' }}>Recent Projects</span>
      <span>Portfolio</span>

      {/* slider */}
      <Swiper
        spaceBetween={30}
        slidesPerView={3}
        grabCursor={true}
        className="portfolio-slider"
      >
        <SwiperSlide>
          <div className="portfolio-item">
            <video src={Video1} controls />
            <button onClick={() => openInNewTab('https://example.com/video1')}>View Project</button>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="portfolio-item">
            <video src={Video2} controls />
            <button onClick={() => openInNewTab('https://example.com/video2')}>View Project</button>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="portfolio-item">
            <video src={Video3} controls />
            <button onClick={() => openInNewTab('https://example.com/video3')}>View Project</button>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="portfolio-item">
            <video src={Video4} controls />
            <button onClick={() => openInNewTab('https://example.com/video4')}>View Project</button>
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="portfolio-item">
            <video src={Video5} controls />
            <button onClick={() => openInNewTab('https://example.com/video5')}>View Project</button>
          </div>
        </SwiperSlide>
      </Swiper>
    </div>
  );
};

export default Portfolio;
